import React, { Component } from "react";

class Index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mushrooms: []
    };
  }
  componentDidMount() {
    this.loadMushrooms();
  }

  loadMushrooms = () => {
    const url = "http://localhost:5000/mushroom";
    fetch(url)
      .then(resp => resp.json())
      .then(data => {
        this.setState({
          mushrooms: data
        });
      });
  };

  bookList = () => {
    return (
      <div className="book-list">
        {this.state.books.map((item, index) => (
          <div key={index} className="book">
            <div onClick={() => this.handleEditClick(item)} key={index}>
              {item.genre}: {item.title} by {item.author} (rrp ${item.price})
              (id: {item.id})
            </div>
          </div>
        ))}
      </div>
    );
  };

  render() {
    if (!this.state.books.length) {
      return (
        <div className="spinner">
          <ReactLoading
            type={"spin"}
            color={"#e6e6e6"}
            height={667}
            width={375}
          />
        </div>
      );
    } else {
      return (
        <div>
          <h1>Books {this.state.books.length}</h1>

          {this.state.addBookAlert ? (
            <Alert variant="success" onClose={this.handleDismiss} dismissible>
              <Alert.Heading>Success adding book!</Alert.Heading>
            </Alert>
          ) : null}

          {this.state.updateBookAlert ? (
            <Alert variant="success" onClose={this.handleDismiss} dismissible>
              <Alert.Heading>Success updating Book!</Alert.Heading>
            </Alert>
          ) : null}

          {this.state.adding ? (
            <BookForm createAndUpdateBook={this.createAndUpdateBookHandler} />
          ) : this.state.selectedBook ? (
            <BookForm
              createAndUpdateBook={this.createAndUpdateBookHandler}
              title={this.state.selectedBook.title}
              author={this.state.selectedBook.author}
              genre={this.state.selectedBook.genre}
              price={this.state.selectedBook.price}
              id={this.state.selectedBook.id}
            />
          ) : (
            this.bookList()
          )}

          {!this.state.adding && !this.state.selectedBook ? (
            <button onClick={this.handleClick}>Add</button>
          ) : null}
        </div>
      );
    }
  }
}

export default Index;
