import React from "react";
import Image from "./Image";

const IndexCard = props => {
  let {
    common_name,
    binomial_name,
    division,
    mush_class,
    family,
    order,
    description,
    mycology,
    images
  } = props.mushroom;

  let {
    cap_type,
    hymenium_shape_type,
    hymenium_spore_type,
    stipe_type,
    spore_print,
    ecology,
    edibility_type
  } = mycology;

  const commonNames = common_name.map((name, index) => (
    <div key={index}>{name}</div>
  ));

  // const mushImages = images.map((img, index) => {
  //   <div key={index}>
  //     <Image src={img} width={330} height={280} mode="fit" />
  //   </div>;
  // });

  // const mushImages = images.map((img, index) => {
  //   <div key={index}>
  //     <img src={img} alt=""/>
  //   </div>;
  // });

  return (
    <React.Fragment>
      <div className="card">
        <h1 className="italic">{binomial_name}</h1>
        <div className="mush-names">{commonNames}</div>

        <div className="italic mush-about">{description} </div>
        <div className="mush-text">{division}</div>
        <div className="mush-text"> {mush_class}</div>
        <div className="mush-text"> {family}</div>
        <div className="mush-text"> {order}</div>
        <h2>Mycology</h2>
        <div className="mush-text">
          <span className="bold">Cap Type: </span>
          {cap_type}
        </div>
        <div className="mush-text">
          <span className="bold">Hymenium Shape: </span>
          {hymenium_shape_type}
        </div>
        <div className="mush-text">
          <span className="bold">Hymenium Spore Type: </span>
          {hymenium_spore_type}
        </div>
        <div className="mush-text">
          <span className="bold">Spore Print: </span>
          {spore_print}
        </div>
        <div className="mush-text">
          <span className="bold">Stipe:</span> {stipe_type}
        </div>
        <div className="mush-text">
          <span className="bold">Ecology:</span> {ecology}
        </div>
        <div className="mush-text">
          <span className="bold">Edibility:</span>
          {edibility_type}
        </div>
        {/* <div>{mushImages}</div> */}
        <Image src={images[0]} width={330} height={280} mode="fit" />
      </div>
    </React.Fragment>
  );
};

export default IndexCard;
