import React from "react";
import Image from "./Image";

const IndexCard = mushroom => {
  // common_name is an array of strings as the names
  // and mycology is an object with structure
  // mycology = {
  //     cap_type: "cap is flat or convex";
  //     edibility_type: "edibility: edible";
  //     hymenium_shape_type: "hymenium is adnate or decurrent";
  //     hymenium_spore_type: "pores on hymenium";
  //     stipe_type: "stipe is bare";
  // }

  let {
    common_name,
    binomial_name,
    division,
    mush_class,
    family,
    order,
    description,
    mycology,
    images
  } = mushroom;
  return (
    <React.Fragment>
      <div className="card">
        <h3>{binomial_name}</h3>
        {common_name.map(name => {
          <h4 className="center-text">{name}</h4>;
        })}
        <div className="italic project-about">{description} </div>
        <div className="project-text">{division}</div>
        <div className="project-text"> {mush_class}</div>
        <div className="project-text"> {family}</div>
        <div className="project-text"> {order}</div>
        {images.map(image => {
          <Image src={image} width={500} height={350} mode="fit" />;
        })}
      </div>
    </React.Fragment>
  );
};

export default IndexCard;
